/*** Custom css for the ice shard theme ***/

/* Removes all round edges on everything */
:root {
  --border-radius: 0;
  --border-radius-half: 0;
}

/* Adds a light blue line between the server and channel list */
.dGrZZ {
  border-right: 2px solid #72F5F3;
}

/* Makes spoilers a lighter color, to make them better visible on the dark background */
:root .spoiler {
  background: #313152;
}

/* Hides the wave used as a chosen server indicator by default */
.QgupM span, .kGANuc span {
  display: none;
}

/* Adds a light blue border around the active server's icon */
.QgupM svg, .kGANuc svg {
  outline-style: solid;
  outline-color: #72F5F3;
  outline-width: 2px;
}

/* Colored outline and name of picked channel */
._compact_12e90_19[data-active=true],
._normal_12e90_16[data-active=true],
._user_12e90_23[data-active=true] {
  color: #37FFF8;
  outline-style: solid;
  outline-color: #72F5F3;
  outline-width: 2px;
}

/* Mention message colored + bold text */
div.CaijV > div.jgcjMZ > ._markdown_8b8eo_2,
div.cCwoWr > div.jgcjMZ > ._markdown_8b8eo_2 {
  color: #4CFAFF;
  font-weight: bold;
}

/* Nice home screen */
._home_1khrk_1 {
  height: 100%;
  background-image: url("https://autumn.revolt.chat/attachments/hqI1nk7NBECh8n7GP3eqt-PXv-5tXGWGnbn9qJWZUO");
  background-repeat: no-repeat;
  background-attachment: fixed;
  background-size: 100% 100%;
}
/* Outlining buttons on home screen for better readability */
div._actions_1khrk_12 > a > div,
div._actions_1khrk_12 > div > a > div {
  outline-style: solid;
  outline-color: #72F5F3;
  outline-width: 1px;
}

/* Nicer link hovers */
p>a:hover,span>a:hover,li>a:hover,strong>a:hover,th>a:hover,td>a:hover,
h1>a:hover,h2>a:hover,h3>a:hover,h4>a:hover,h5>a:hover,h6>a:hover{
  color: magenta;
  background: cyan;
  text-decoration: none !important;
}

/* Nicer name hovers */
.author:hover,.user>span:hover{
  background: cyan !important;
  text-decoration: none !important;
  color: #000056;
}

/* Readable buttons on pop-up menus and bot badges */
.ZAptB, .iYfESC, .kqSFkj {
  color: #000056;
  font-weight: bold;
}

/* Theme consistent colors on message search */
.kRnBUt {
  color: #000056;
  font-weight: bold;
  background: #00FFF5;
}
.kRnBUt:hover {
  background: #4CFAFF;
}
.jqHpRY {
  color: #00FFF5;
}

/* buttons and checkboxes */
.check, .dvKiPb {
  outline-style: solid;
  outline-color: #72F5F3;
  outline-width: 1px;
}

/***** END *****/

/*Original theme: <@01FE43S0TRRR73MEP5Y63Z9MCB>(@Davari)
Edits to make more usable made by <@01FTN0QWKA45VG5CPCVJH7H399>(@MYT)
*/
* {
  border-color: var(--border-color) !important;
}
@font-face {
    font-family: Blank;
    src: url("https://raw.githack.com/adobe-fonts/adobe-blank/master/AdobeBlank.ttf.woff?raw=true");
    unicode-range: U+61-7a,U+41-5a,U+20
}
:root {
    font-variant-ligatures:normal!important;
    --font: var(--monospace-font) !important;
    --irc-chat-width: 260px;
}
[class^=MessageBase-] {
    position: relative;
    margin-top: 0;
}
[class^=MessageBase__MessageInfo] {
    width: var(--irc-chat-width);
    margin-right: 10px;
}
[class^=MessageBase__MessageInfo]>svg, .user>svg {
    display: none;
}
[class^=Channel__ChannelContent] [class^=MessageBase__MessageContent] {
    position: unset;
}
[class^=MessageBase__MessageContent]>.detail {
    position: absolute;
    left: 0;
    top: 0;
    width: var(--irc-chat-width);
    display: flex;
    flex-direction: row-reverse;
}
[class^=MessageBase__MessageContent]>.detail>.author {
    word-break: break-all;
}
[class^=MessageBase__MessageInfo]>time {
    opacity: 1 !important;
    flex-grow: 1;   
}
[class^=MessageReply__ReplyBase] {
    margin-left: calc(var(--irc-chat-width) + 15px);
    margin-top: 0;
}
[class^=MessageReply__ReplyBase]::before {
    content: '>';
    border: none;
    align-self: center;
    width: auto;
    height: 100%;
}
[class^=MessageBase__DetailBase] {
    margin-right: auto;
}
[class^=MessageBase__DetailBase]>time {
    margin-left: 2px;
    font-family: Blank, var(--font);
}
[class^=MessageBase__]>time::before {
    content: '['
}
[class^=MessageBase__]>time::after {
    content: ']'
}
[class^=MessageBase__MessageContent]>.detail>.author::before, .user>span::before {
    content: '<'
}
[class^=MessageBase__MessageContent]>.detail>.author::after, .user>span::after {
    content: '>'
}
[class^=DateDivider__Base] {
    margin: 3px -6px;
    padding: 1px 4px;
    position: sticky;
    top: 0;
    border: unset;
    background: var(--secondary-header);
    height: auto;
    z-index: 1;
}
[class^=DateDivider__Base]>time {
    font-size: 10px;
    background: unset;
}
[class^=MessageArea__Area] {
    background: linear-gradient(to right, var(--secondary-background) calc(var(--irc-chat-width) + 7px), #0000 calc(var(--irc-chat-width) + 7px))
}
/*[class^=MessageArea__Area]>div {
    padding-bottom: 0;
}
to reactivate this, you need to hide the * is typing bar*/
[class^=DateDivider__Unread] {
    padding: unset;
    border-radius: unset;
    background: unset;
    color: var(--accent);
    font-size: 10px;
    margin: 0 5px;
}
[class^=DateDivider__Unread]::after {
    content: '';
    position: absolute;
    width: 100%;
    background: var(--accent);
    height: 1px;
    top: 7px;
    margin-left: 5px;
}
[class^=Channel__ChannelContent] [class^=MessageBase__MessageInfo]>.copyTime {
    position: relative;
}

